<template>
    <div class="team-meeting-details" :class="layout">
        <topbar-simple v-if="layout == 'window'" class="popout-meeting-details-titlebar" :title="windowtitle"
            :buttons="['close', 'minimize', 'maximize']" @close="close" />
        <div class="top">
            <div v-if="layout == 'panel'" class="controls">
                <div v-if="layout == 'panel'" class="btn-titbar" @click="popout">
                    <wl-icon name="pop-out-bold" size="16" />
                </div>
                <div class="btn-titbar" @click="close">
                    <wl-icon name="cancel-bold" size="16" />
                </div>
            </div>

            <div class="head">
                <div class="title">
                    {{ selectedMeeting.title }}
                    <md-button aria-label="button" size="28" class="md-button-outline btn-circle" style="margin-left: 8px;" @click="toggleFollow">
                        <wl-icon :name="!isFollow ? 'flag-bold' : 'flag-filled'" size="16" />
                     </md-button>
                </div>

                <div class="rgt">
                    <md-button aria-label="button" size="28" class="md-button-outline btn-icon">
                        <wl-icon name="forward-message-bold" size="16" />
                    </md-button>
                    <md-button v-if="layout == 'window'" aria-label="button" size="28" class="btn md-button-outline-green" @click="meet(selectedMeeting, $event, inProgress(selectedMeeting.start, selectedMeeting.end))">
                        {{inProgress(selectedMeeting.start, selectedMeeting.end) || inMinutes(selectedMeeting.start) ? 'Join' : 'Start'}}
                    </md-button>
                </div>
            </div>

            <div class="time">
                <span class="lb">{{ selectedMeeting.start !== 'anytime' ? `${$formatDate(selectedMeeting.start, 'EEEE, LLLL d, y')}` : 'Anytime' }}</span>
                <wl-icon v-if="selectedMeeting.start !== 'anytime'" name="dot-divider" size="4" class="divider" />
                <span v-if="selectedMeeting.start !== 'anytime'" class="lb">{{ `${$formatDate(selectedMeeting.start,
                    'kk:mm')} - ${$formatDate(selectedMeeting.end, 'kk:mm')}` }}</span>
            </div>
            <div v-if="selectedMeeting.team && layout == 'panel'" class="team text-link underline">
                {{ selectedMeeting.team }}
            </div>

            <div v-if="layout == 'panel'" class="btnrow">
                <md-button aria-label="button" color="green" size="28" class="btn" @click="meet(selectedMeeting, $event, inProgress(selectedMeeting.start, selectedMeeting.end))">
                    {{inProgress(selectedMeeting.start, selectedMeeting.end) || inMinutes(selectedMeeting.start) ? 'Join' : 'Start'}}
                </md-button>
                <md-button aria-label="button" color="blue" size="28" class="btn" >
                    Collaborate
                </md-button>

                <span class="spacer"></span>

                <md-button aria-label="button" size="28" class="md-button-outline btn-icon">
                    <wl-icon name="pen-bold" size="16" />
                </md-button>
                <md-button aria-label="button" size="28" class="md-button-outline btn-icon">
                    <wl-icon name="forward-message-bold" size="16" />
                </md-button>
                <md-button v-if="!isPostMeeting" aria-label="button" size="28" class="md-button-outline btn-icon">
                    <wl-icon name="delete-bold" size="16" />
                </md-button>
            </div>

            <div class="tabs">

                <div class='meeting-details-tabs' @mouseleave="leaveTab">
                    <div class="meeting-details-tabs-active-bg"></div>
                    <div v-for="(tab, idx) in tabs" :key="`tab${idx}`" class="meeting-details-tabs-tab" :class="[{
                        active: activeTabId === idx,
                    }, `meeting-details-tabs-tab${idx}`]" @mouseover="hoverTab(idx)" @click="selectTab(idx)">
                        <span v-if="tab.icon" v-html="tab.icon" style="margin-right:4px;" ></span>
                        <span class="lb">{{ tab.label }}</span>
                    </div>
                </div>

                <!--md-button v-if="layout == 'window' && calendarMeetingsConnected" aria-label="button" size="28"
                    class="icobutton md-button-outline">
                    <wl-icon name="chat-group-bold" size="16" class="ico" />
                    Create a space
                </md-button-->

            </div>
        </div>

        <div class="contentarea">
            <div v-if="layout == 'window'" class="rightcol">
                <wl-scrollbar v-if="calendarMeetingsConnected" ref="scrollbar3"
                    :classes="`rightcol-scrollarea`" :theme="theme === 'dark' ? 'dark' : 'light'"
                    :arrowVisible="$store.state.os === 'win'">

                    <div v-if="activeTabVal === 'about'" class="rightcol-scrollarea-content">
                        <div class="subtitlegrp">
                            <span class="subtitle">Invitees ({{`${selectedMeeting.accepted.length + selectedMeeting.waiting.length}`}})</span>
                            <md-button aria-label="button" size="28" class="md-button-outline">
                                Copy
                            </md-button>
                        </div>

                        <md-input 
                            class="md-search-input search searchinput" 
                            ref="searchinput"
                            clear
                            shape="pill"
                            placeholder="Add by name or email"
                            >
                            <wl-icon
                                class="md-search-input__icon"
                                name="search-bold"
                                size="16"
                                slot="inputBefore"
                            />
                            <wl-icon
                                name="cancel-bold" 
                                :color=" theme === 'light' ? 'black-95' : 'white-95' "
                                size="16"
                                slot="clear"
                            />
                        </md-input>

                        <people-item
                            v-for="(p, index) in inviteeList"
                            :key="`in${index}`"
                            :type="p.type"
                            :name="p.name"
                            :avatar="p.avatar"
                            :icon="p.type === 'room' ? 'generic-device-video-regular' : null"
                            iconColor="white"
                            height="48px"
                            :iconSize="32"
                            :status="p.status"
                            :activeInfo="index===0 ? 'Organzier' : (index < selectedMeeting.accepted.length ? 'Accepted' : 'Waiting for response')"
                        />

                    </div>

                    <div v-if="activeTabVal === 'summary'" class="rightcol-scrollarea-content">
                        <div class="subtitlegrp">
                            <span class="subtitle">Recordings (3)</span>
                        </div>

                        <recording-reel :selectedMeeting="selectedMeeting" :datalist="recordingDatalist" @play="openRecordingPlayer"
                            @change="obj => { this.smartChapter = obj.smartChapter }" />

                        <div class="divider-h"></div>

                        <div class="block">
                            <div class="blocktt">
                                <span class="lb">Smart chapters</span>
                            </div>
                            <div v-for="(itm, idx) in smartChapter" :key="`key${idx}`" class="scitem"
                                @click="openRecordingPlayer">
                                <div class="name">{{ itm.name }}</div>
                                <div class="ts">{{ itm.timespan }}</div>
                                <div class="btnplay">
                                    <wl-icon name="play-bold" size="16" />
                                </div>
                            </div>
                        </div>
                    </div>

                </wl-scrollbar>
            </div>
            <div class="leftcol">
                <wl-scrollbar v-if="!calendarMeetingsConnected" ref="scrollbar2" :classes="'teams-meetings-split-content'"
                    :theme="theme === 'dark' ? 'dark' : 'light'" :arrowVisible="$store.state.os === 'win'">
                    <div class="scrollcontent-wrap">

                        <div class="accepted">
                            <span class="subtitle">Invitees ({{ `${selectedMeeting.accepted.length + selectedMeeting.waiting.length}` }})</span>
                        </div>
                        <div class="accepted-avatars">
                            <presence-avatar v-for="(p, idx) in selectedMeeting.accepted" :key="`acc${idx}`" :title="p"
                                :src="people(p).avatar ? avatarPath + people(p).avatar : ''" :color="'white'"
                                :backgroundColor="'#545454'" :size="32" :presence="people(p).status"
                                :theme="theme === 'light' ? 'light' : 'dark'" class="avatar" />
                            <span class="lb">{{`${selectedMeeting.accepted.length}/${selectedMeeting.accepted.length + selectedMeeting.waiting.length}`}} {{ isPostMeeting ? 'Attended' : 'Accepted' }}</span>
                        </div>

                        <div class="subtitle">Agenda</div>
                        <div class="des">Take a chance to check out the latest meeting experience.</div>

                        <div class="subtitle">Attachment</div>
