                            name: 'Stand up',
                            timespan: '00:22:00 - 00:24:30',
                            thumb: 'tmp_recording_thumb_3.png',
                        },
                    ],
                },
                {
                    img: 'tmp_recording_thumb_1.png',
                    name: '_Recording',
                    date: '2022/11/24',
                    time: '10:04',
                    dur: '24min',
                    smartChapter: [
                        {
                            name: 'Intro',
                            timespan: '00:00:00 - 00:02:59',
                            thumb: 'tmp_recording_thumb_3.png',
                        },
                        {
                            name: 'Show and Share',
                            timespan: '00:03:00 - 00:11:59',
                            thumb: 'tmp_recording_thumb_3.png',
                        },
                        {
                            name: 'Feedback',
                            timespan: '00:12:00 - 00:21:59',
                            thumb: 'tmp_recording_thumb_3.png',
                        },
                        {
                            name: 'Stand up',
                            timespan: '00:22:00 - 00:24:30',
                            thumb: 'tmp_recording_thumb_3.png',
                        },
                    ],
                },
            ],
            smartChapter: [],

        };
    },
    computed: {
        os() {
            return this.$store.state.os;
        },
        theme() {
            return this.$store.state.theme;
        },
        themeColor() {
            return this.$store.state.themeColor;
        },
        themeColorList() {
            return this.$store.state.themeColorList;
        },
        me() {
            return this.$store.getters.me;
        },
        tabs() {
            return !this.isPostMeeting ? this.tabs1 : this.tabs2;
        },
        isPostMeeting() {
            return this.$isPastDate(this.selectedMeeting.end)
        },
        teamsMeetingList() {
            return this.$store.state.teamsMeetingList;
        },
        calendarMeetingsConnected() {
            return this.$store.state.calendarMeetingsConnected;
        },
        avatarPath() {
            return this.$store.state.avatarPath;
        },
        contentColor() {
            return this.themeColorList[this.theme][this.themeColor]['content'];
        },
        inviteeList() {
            let names = this.selectedMeeting.accepted.concat(this.selectedMeeting.waiting)
            var list = []
            for (var i=0; i<names.length; i++) {
                var n = names[i]
                list.push(this.$store.getters.people(n)) 
            }
            return list;
        },

    },
    mounted() {
        if (this.isPostMeeting) {
            this.selectTab(1)
        }

        this.isFollow = this.selectedMeeting.follow
        this.reponseType = this.selectedMeeting.reponseType

        this.smartChapter = this.recordingDatalist[0].smartChapter

        if (this.jumpto === 'actionitems') {
            requestAnimationFrame(() => {
                const rect = this.$refs['scrollcontent-summary'].getBoundingClientRect()
                const rect2 = this.$refs.actionitems.getBoundingClientRect()
                this.$refs.scrollbar2.scrollToY(rect2.top - rect.top, false);
            })
        }
    },
    watch: {
        selectedMeeting(val) {
            this.isFollow = val.follow
            this.reponseType = val.reponseType
        },
        isPostMeeting(val) {
            if (val) {
                this.$nextTick(() => {
                    this.selectTab(1)
                })
            }else{
                this.$nextTick(() => {
                    this.selectTab(0)
                })
            }
        },
    },
    methods: {
        inProgress(d1, d2) {
            let res = false
            if (this.$isPastDate(d1) && this.$isFutureDate(d2)) {
                res = true
            }
            return res
        },
        inMinutes(d1) {
            let res = false
            let d = addMinutes(d1, -15)
            if (this.$isPastDate(d) && this.$isFutureDate(d1)) {
                let sec = getUnixTime(d1) - getUnixTime(new Date())
                res = Math.ceil(sec / 60)
            }

            return res
        },
        people(name) {
            return this.$store.getters.people(name);
        },
        hoverTab(id) {
            this.moveTabBg(id);
        },
        leaveTab() {
            this.moveTabBg(this.activeTabId);
        },
        selectTab(id) {
            this.activeTabId = id;
            this.activeTabVal = this.tabs[id].val
            this.moveTabBg(id);

            this.$refs.scrollbar2.update();
            this.$refs.scrollbar2.scrollToY(0, true);
        },
        moveTabBg(id) {
            const rect = document.querySelector(`.${this.layout} .meeting-details-tabs-tab${id}`).getBoundingClientRect();
            const rect2 = document.querySelector(`.${this.layout} .meeting-details-tabs`).getBoundingClientRect();
            document.querySelector(`.${this.layout} .meeting-details-tabs-active-bg`).style.left = `${rect.left - rect2.left}px`;
            document.querySelector(`.${this.layout} .meeting-details-tabs-active-bg`).style.width = `${rect.width}px`;
        },
        meet(info, event, late) {
            this.$popper.hideAll();

            if (event) {
                event.stopPropagation();
            }
            this.$store.commit('meetingTitle', info.title);
            this.$store.commit('interstitialParams', { visible: true, host: false, late: late });
        },
        popout() {
            if (this.onPopout) {
                this.onPopout()
            }
            this.$emit("popout");
            this.close();
        },
        close() {
            if (this.onClose) {
                this.onClose()
            }
            this.$emit("close");
        },
        openRecordingPlayer() {
            this.$eventBus.$emit('open-recording-player-window', {
                meetingInfo: this.selectedMeeting,
                smartChapter: this.smartChapter,
            }, {})
        },
        toggleClsContent(idx) {
            this.clsContentVisible[idx] = !this.clsContentVisible[idx]
        },

        toggleFollow() {
            this.followMeeting(!this.isFollow)
        },
        followMeeting(val) {
            this.isFollow = val
            let meetingid = this.selectedMeeting.id

            let mtlst = this.teamsMeetingList.slice()
            for(let i=0; i<mtlst.length; i++){
                let dd = mtlst[i]
                let ddmeetings = dd.meetings.slice()
                for(let j=0; j<ddmeetings.length; j++){
                    if (ddmeetings[j].id == meetingid) {
                        ddmeetings[j].follow = val
                    }
                }
                dd.meetings = ddmeetings
            }
            this.$store.commit('teamsMeetingList', mtlst);
        },
        decline() {
            this.reponseType = 'declined'

            let meetingid = this.selectedMeeting.id
            let mtlst = this.teamsMeetingList.slice()
            for(let i=0; i<mtlst.length; i++){
                let dd = mtlst[i]
                let ddmeetings = dd.meetings.slice()
                for(let j=0; j<ddmeetings.length; j++){
                    if (ddmeetings[j].id == meetingid) {
                        ddmeetings[j].reponseType = this.reponseType
                    }
                }
                dd.meetings = ddmeetings
            }
            this.$store.commit('teamsMeetingList', mtlst);
            
        },

        showDeclineAlert() {
            this.showDialog(
                `Do you want to follow this meeting?`, 
                `You will receive the recording and the meeting summary from the host after following this meeting.`,
                [
                {
                    title: 'Decline and follow',
                    class: 'md-button-solid2',
                    func: () => {
                        this.followMeeting(true)
                        this.decline()
                    },
                },
                {
                    title: 'Dont follow',
                    class: 'md-button-outline',
                    func: () => {
                        this.decline()
                    },
                },
                ],
                `#desktop`)
        },

        showDialog(title, message, buttons, parentSelector) {
            var dgwidth = 400
            var dgheight = 188
            var parentel = document.querySelector(parentSelector)
            var rect = parentel.getBoundingClientRect()
            var top = rect.top + (rect.height - dgheight)/2
            var left = rect.left + (rect.width - dgwidth)/2

            this.$modal.show(
            dialog, 
            {
                topbarTitle: 'Webex',
                title: title,
                message: message,
                buttons: buttons,
            }, // component props
            {
                clickToClose: false,
                frameless: false,
                shadow: true,
                resizable: false,
                draggable: '.dialog .topbar',
                overlayVisible: true,
                overlayTransparent: 0,
                theme: this.theme,
                roundedCorner: this.os === 'mac',
                width: dgwidth,
                height: 'auto',
                center: false,
                top: top,
                left: left,
                transition: true,
            }, // modal props
            {
                closed: () => {  }
            } // modal eventlistener
            );
        },

    }
};
</script>

